mapper.insert:
   MERGE /* com.koo.bonscore.biz.store.mapper.GourmetRecordMapper.insertGourmetRecord */
    INTO GOURMET_RECORDS                          /* 맛집 기록 정보 */
   USING DUAL
      ON ( RECORD_ID = #{recordId} )              /* 맛집 기록 ID */
    WHEN MATCHED THEN
  UPDATE
     SET STORE_NAME       = #{name}               /* 가게 이름 */
       , CATEGORY         = #{category}           /* 카테고리 */
       , VISIT_DATE       = #{visitDate}          /* 방문 날짜 */
       , RATING           = #{rating}             /* 별점 */
       , MEMO             = #{memo}               /* 메모 */
       , REFERENCE_URL    = #{referenceUrl}       /* 참조 URL */
       , UPDATED_AT       = #{updatedAt}          /* 수정일시 */
WHEN NOT MATCHED THEN
  INSERT
       (
         RECORD_ID
       , USER_ID
       , STORE_NAME
       , CATEGORY
       , VISIT_DATE
       , RATING
       , MEMO
       , REFERENCE_URL
       , CREATED_AT
       , UPDATED_AT
       )
  VALUES
       (
         #{recordId}
       , #{userId}
       , #{name}
       , #{category}
       , #{visitDate}
       , #{rating}
       , #{memo}
       , #{referenceUrl}
       , #{createdAt}
       , #{updatedAt}
       )

INSERT /* com.koo.bonscore.biz.store.mapper.GourmetRecordMapper.insertGourmetImages */ INTO GOURMET_IMAGES /* 맛집 기록 이미지 파일 정보 */ ( IMAGE_ID /* 이미지 ID */ , RECORD_ID /* 이미지가 속한 맛집 기록 ID */ , ORIGINAL_FILE_NAME /* 원본 파일명 */ , STORED_FILE_NAME /* 저장 파일명 */ , IMAGE_URL /* 다운로드 URL */ , FILE_SIZE /* 파일 크기 (단위: byte) */ , CREATED_AT /* 레코드 생성 일시 */ ) SELECT SEQ_GOURMET_IMAGES.NEXTVAL , A.* FROM (
SELECT #{recordId} AS RECORD_ID , #{image.originalFileName} AS ORIGINAL_FILE_NAME , #{image.storedFileName} AS STORED_FILE_NAME , #{image.imageUrl} AS IMAGE_URL , #{image.fileSize} AS FILE_SIZE , #{createdAt} AS CREATED_AT FROM DUAL
) A
